function createfigure(zdata1,cmaphotlog)
%CREATEFIGURE(ZDATA1)
%  ZDATA1:  surface zdata

%  Auto-generated by MATLAB on 01-Jun-2012 13:35:41

% Create figure
figure1 = figure;

% Create axes
axes1 = axes('Parent',figure1);
view(axes1,[-0.5 90]);
grid(axes1,'on');
hold(axes1,'all');
axis equal
%axis([ 560 650 1180 1400])

% Create surf
surf(zdata1,'Parent',axes1, 'LineStyle','none');

%if exist('cmaps')
    colormap(cmaphotlog);
    grid off
%end
% Create xlabel
xlabel('x','VerticalAlignment','cap','HorizontalAlignment','center');

% Create ylabel
ylabel('y','VerticalAlignment','bottom','Rotation',90,...
    'HorizontalAlignment','center');

% Create zlabel
zlabel('z','Visible','off','VerticalAlignment','top');


